from itertools import zip_longest

def findTheDifference(s: str, t: str) -> str:
    for letter in t:
        if s.count(letter) != t.count(letter):
            return letter

s = "abcd"
t = "abcde"

assert findTheDifference(s,t) == "e"

s = ""
t = "y"

assert findTheDifference(s,t) == "y"

s = ""

t = ""

assert findTheDifference(s,t) == "t"